import * as React from "react";
import CssBaseline from "@mui/material/CssBaseline";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import Toolbar from "@mui/material/Toolbar";
import Paper from "@mui/material/Paper";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import Button from "@mui/material/Button";
import Link from "@mui/material/Link";
import Typography from "@mui/material/Typography";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import AddressForm from "./AddressForm";
import PaymentForm from "./PaymentForm";
import Review from "./Review";
import { Shipped } from "./shipped";
import { useSelector } from "react-redux";
import { useNavigate, Navigate } from "react-router-dom";

function Copyright() {
  return (
    <Typography variant="body2" color="text.secondary" align="center">
      {"Copyright Â© "}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

const steps = [
  "Contact Information",
  "Shipping Method",
  "Payment details",
  "Review your order",
];

function getStepContent(step) {
  switch (step) {
    case 0:
      return <AddressForm />;

    case 1:
      return <Shipped />;

    case 2:
      return <PaymentForm />;

    case 3:
      return <Review />;

    default:
      throw new Error("Unknown step");
  }
}

const theme = createTheme();

export default function Checkout() {
  const [activeStep, setActiveStep] = React.useState(0);
  const state = useSelector((state) => state);
  const navigate = useNavigate();

  const handleNext = () => {
    setActiveStep(activeStep + 1);
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
  };

  if (!state.token) {
    console.log(state.token);
    // Relogin");
    <Navigate to="/login" />;
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar
        position="absolute"
        color="default"
        elevation={0}
        sx={{
          position: "relative",
          borderBottom: (t) => `1px solid ${t.palette.divider}`,
        }}
      >
        <p style={{ margin: "auto" }}>Express checkout</p>
        <Toolbar>
          <Typography variant="h6" color="inherit" noWrap width="100%">
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <div
                style={{
                  height: "50px",
                  width: "30%",
                  background: "#3c0def",
                  borderRadius: "5px",
                  textAlign: "center",
                  cursor: "pointer",
                }}
                onClick={() => {
                  alert("serverDown please try later");
                }}
              >
                <img
                  src="https://image.shutterstock.com/image-photo/paytm-editorial-illustrative-photo-news-600w-2154668717.jpg"
                  height="40"
                  width="50"
                />
              </div>
              <div
                style={{
                  height: "50px",
                  width: "30%",
                  background: "#FFC439",
                  borderRadius: "5px",
                  textAlign: "center",
                  cursor: "pointer",
                }}
                onClick={() => {
                  alert("serverDown please try later");
                }}
              >
                <img src="data:image/svg+xml;base64," />
              </div>
              <div
                style={{
                  height: "50px",
                  width: "30%",
                  background: "black",
                  borderRadius: "5px",
                  textAlign: "center",
                  cursor: "pointer",
                }}
                onClick={() => {
                  alert("serverDown please try later");
                }}
              >
                <img
                  src="https://image.shutterstock.com/image-photo/smart-phone-google-pay-logo-600w-1572992740.jpg"
                  height="40"
                  width="50"
                />
              </div>
            </div>
          </Typography>
        </Toolbar>
      </AppBar>
      <Container component="main" maxWidth="sm" sx={{ mb: 4 }}>
        <Paper
          variant="outlined"
          sx={{ my: { xs: 3, md: 6 }, p: { xs: 2, md: 3 } }}
        >
          <Typography component="h1" variant="h4" align="center">
            Checkout
          </Typography>
          <Stepper activeStep={activeStep} sx={{ pt: 3, pb: 5 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
          <React.Fragment>
            {activeStep === steps.length ? (
              <React.Fragment>
                <Typography variant="h5" gutterBottom>
                  Thank you for your order.
                </Typography>
                <Typography variant="subtitle1">
                  Your order number is #2001539. We have emailed your order
                  confirmation, and will send you an update when your order has
                  shipped.
                </Typography>
              </React.Fragment>
            ) : (
              <React.Fragment>
                {getStepContent(activeStep)}
                <Box sx={{ display: "flex", justifyContent: "flex-end" }}>
                  {activeStep !== 0 && (
                    <Button onClick={handleBack} sx={{ mt: 3, ml: 1 }}>
                      Back
                    </Button>
                  )}

                  <Button
                    variant="contained"
                    onClick={handleNext}
                    sx={{ mt: 3, ml: 1 }}
                    style={{ background: "black", height: "50px" }}
                  >
                    {activeStep === steps.length - 1
                      ? "Place order"
                      : steps.length - 1
                      ? "Continue to Payment"
                      : "Continue to Shipping"}
                  </Button>
                </Box>
              </React.Fragment>
            )}
          </React.Fragment>
        </Paper>
        <Copyright />
      </Container>
    </ThemeProvider>
  );
}
